def threesum(arr):
    #target is 0
    res = []
    n = len(arr)
    arr.sort()
    def twosum(low, high, target):
        while low<high:
            if arr[low]+arr[high] == target:
               res.append([arr[low],arr[high],-target])
               while low<high and arr[low] == arr[low+1]: low += 1
               while low<high and arr[high] == arr[high-1]: high -= 1
               low += 1
               high -= 1
            elif arr[low]+arr[high] < target: low += 1
            else: high -= 1

    for i in range(n-2):
        if i == 0 or i>0 and arr[i-1] != arr[i]:
            twosum(i+1, n-1, -arr[i])
    return res


def nsum(arr, target):
    #apply to n-sum with target
    res = []
    arr.sort()

    def ksum(nums, target, k, result, value):
        if k == 2:
            low, high = 0, len(nums) -1
            while low < high:
                if nums[low] + nums[high] == target:
                    result.append(value + [nums[low], nums[high]])
                    while low < high and nums[low] == nums[low + 1]: low += 1
                    while low < high and nums[high] == nums[high - 1]: high -= 1
                    low += 1
                    high -= 1
                elif nums[low] + nums[high] < target:
                    low += 1
                else:
                    high -= 1
        elif k > 2:
            for i in range(len(nums)-k+1):
                if target < nums[i] * k or target > nums[-1] * k:  # optimize redundant calculation
                    break
                if i == 0 or i>0 and nums[i-1] != nums[i]:
                    ksum(nums[i+1:], target-nums[i], k-1, result, value+[nums[i]])

    ksum(arr, target, 4, res, [])
    return res

import collections
def threeSumMulti(A, target):
    """
    :type A: List[int]
    :type target: int
    :rtype: int
    """
    mod = 10**9+7
    res = 0
    col = collections.Counter(A)
    a = list(col)
    a.sort()
    memo = []
    for i in col:
        n1 = col[i]
        col[i] -= 1
        for j in col:
            n2 = col[j]
            if col[j] != 0:
                col[j] -= 1
                for k in col:
                    n3 = col[k]
                    if i+j+k == target and col[k] != 0 and (i,j,k) not in memo:
                        res += n1*n2*n3
                        memo.append((i,j,k))
                col[j] += 1
        col[i] += 1

    return res % mod


